package bedrock_test

import (
	"context"
	"fmt"
	"testing"

	"github.com/aws/aws-sdk-go-v2/config"
	"github.com/aws/aws-sdk-go-v2/service/bedrockruntime"
	. "github.com/sansmoraxz/llama-index-go/integrations/embeddings/bedrock"
)


const sampleText = "hello world"
const sampleImage = ""

func TestTitan(t *testing.T) {
	ctx := context.Background()
	cfg, err := config.LoadDefaultConfig(ctx)
	if err != nil {
		t.Fatal(err)
	}
	client := bedrockruntime.NewFromConfig(cfg)
	embedding, err := FetchAmazonTextEmbeddings(ctx, client, MODEL_TITAN_EMBED_G1, sampleText)
	if err != nil {
		t.Fatal(err)
	}

	fmt.Println(embedding)
}

func TestTitanImage(t *testing.T) {
	ctx := context.Background()
	cfg, err := config.LoadDefaultConfig(ctx)
	if err != nil {
		t.Fatal(err)
	}
	client := bedrockruntime.NewFromConfig(cfg)
	embedding, err := FetchAmazonMultiModalEmbeddings(ctx, client, MODEL_TITAN_MULTIMODAL_EMBED_G1, sampleText , sampleImage)
	if err != nil {
		t.Fatal(err)
	}

	fmt.Println(embedding)
}

func TestCohere(t *testing.T) {
	ctx := context.Background()
	cfg, err := config.LoadDefaultConfig(ctx)
	if err != nil {
		t.Fatal(err)
	}
	client := bedrockruntime.NewFromConfig(cfg)
	embedding, err := FetchCohereTextEmbeddings(ctx, client, "cohere.embed-english-v3", []string{sampleText}, COHERE_INPUT_TYPE_TEXT)
	if err != nil {
		t.Fatal(err)
	}

	fmt.Println(embedding)
}
